#!/bin/sh

URL="http://localhost:8545";

GachaFactory=""

from="0x0D7134fe1Fff95780f05c6751dc4514cDf0134E7"
gas="0x58000";
id=$RANDOM;


curl -s -X POST -H "Content-Type: application/json" --data '{
    "jsonrpc":"2.0",
    "id":"'"$id"'",
    "method":"eth_sendTransaction",
    "params":[{
        "from":"'"$from"'",
        "gas":"'"$gas"'",
        "data":"'"$GachaFactory"'"
    }]
}' $URL

